s = {}s[ "head" ] = 10s[ "/" ] = 20s[ "\\\\" ] = 20s[ "title" ] = 10s[ "body" ] = 10s[ "tr" ] = 100s[ "td" ] = 100s[ "table" ] = 30s[ ">" ] = 200s[ "<" ] = 200s[ "&" ] = 20s[ ";" ] = 20for i = 1, 255 do  s[ "\\x" .. string.format( "%x", i .. "" ) ] = 1endprint( "const char* fuzz[] = {\n  \"\\x0\"" )for k, v in pairs( s ) do  print( ",\n  " )  for i = 1, v do    print( "\"" .. k .. "\"" )    if i ~= v then print( ", " ) end  endendprint "\n}"